mapper.select:
SELECT PROJ_ID , MEM_CODE , PROJ_NAME , PROJ_STRT_DATE , PROJ_END_DATE , PROJ_SMRY , PROJ_OPN_WHTH , PROJ_CREATN_DATE , PROJ_IMG_ROUTE , DELETE_WHTH FROM ( SELECT ROWNUM RN , A.PROJ_ID , A.MEM_CODE , A.PROJ_NAME , A.PROJ_STRT_DATE , A.PROJ_END_DATE , A.PROJ_SMRY , A.PROJ_OPN_WHTH , A.PROJ_CREATN_DATE , A.PROJ_IMG_ROUTE , A.DELETE_WHTH FROM( SELECT PROJ_ID , (SELECT M.MEM_NAME FROM MEM M WHERE P.MEM_CODE = M.MEM_CODE) AS MEM_CODE , PROJ_NAME , PROJ_STRT_DATE , PROJ_END_DATE , PROJ_SMRY , PROJ_OPN_WHTH , PROJ_CREATN_DATE , PROJ_IMG_ROUTE , DELETE_WHTH FROM PROJ P
WHERE PROJ_NAME LIKE #{keyword}
WHERE MEM_CODE IN (SELECT MEM_CODE FROM MEM WHERE MEM_NAME LIKE #{keyword})
ORDER BY PROJ_CREATN_DATE DESC) A )
WHERE RN > ( #{pageNum} - 1 ) * #{amount} AND RN <= #{pageNum} * #{amount}

SELECT COUNT(PROJ_ID)
FROM PROJ


		WHERE PROJ_NAME LIKE #{keyword}


		WHERE MEM_CODE LIKE #{keyword}

mapper.update:
UPDATE PROJ
SET DELETE_WHTH = 'y'
WHERE PROJ_ID = #{projId}

UPDATE PROJ
SET DELETE_WHTH = 'n'
WHERE PROJ_ID = #{projId}
