mapper.select:

select mem_code,
       proj_id
from PROJ_MEM
where proj_id = #{projId}
  AND MEM_CODE <> #{memCode}
  AND dropstatus <> 'Y'


select MEM_CODE, PROJ_ID
from proj_mem
where dropstatus = 'N'
  AND MEM_CODE <> #{memCode}
  AND PROJ_ID = #{projId}

mapper.update:
UPDATE PROJ_MEM
SET DROPSTATUS='Y'
WHERE proj_id = #{projId}
  AND mem_code = #{memCode}
  AND DROPSTATUS = 'N'

mapper.delete:
DELETE
FROM proj_mem
WHERE proj_id = #{projId}
  AND MEM_CODE = #{memCode}

mapper.select:
select count(*)
from alrm
where mem_code = #{memCode}
