mapper.select:
SELECT MEM_CODE , MEM_EMAIL , MEM_PASSWD , MEM_NAME , MEM_PHONE_NUMBER , MEM_SGNUP_DATE , MEM_IMG_ROUTE , MEM_WHDRL_YN , (SELECT COUNT(P.DROPSTATUS)FROM PROJ_MEM P WHERE P.DROPSTATUS = 'Y' AND B.MEM_CODE = P.MEM_CODE) AS MEM_PROJ_QUIT FROM ( SELECT ROWNUM RN , MEM_CODE , MEM_EMAIL , MEM_PASSWD , MEM_NAME , MEM_PHONE_NUMBER , MEM_SGNUP_DATE , MEM_IMG_ROUTE , MEM_WHDRL_YN , MEM_PROJ_QUIT FROM(SELECT MEM_CODE , MEM_EMAIL , MEM_PASSWD , MEM_NAME , MEM_PHONE_NUMBER , MEM_SGNUP_DATE , MEM_IMG_ROUTE , MEM_WHDRL_YN , MEM_PROJ_QUIT FROM MEM
WHERE MEM_EMAIL LIKE #{keyword}
WHERE MEM_NAME LIKE #{keyword}
WHERE MEM_CODE LIKE #{keyword}
) A ) B
WHERE RN > ( #{pageNum} - 1 ) * #{amount} AND RN <= #{pageNum} * #{amount}

SELECT MEM_EMAIL, MEM_CODE, MEM_NAME, MEM_PHONE_NUMBER, MEM_SGNUP_DATE
FROM MEM
WHERE MEM_CODE = #{memCode}

SELECT COUNT(MEM_EMAIL)
FROM MEM


		WHERE MEM_EMAIL LIKE #{keyword}


		WHERE MEM_NAME LIKE #{keyword}


		WHERE MEM_CODE LIKE #{keyword}

mapper.update:
UPDATE MEM
SET MEM_WHDRL_YN = 'y'
WHERE MEM_EMAIL = #{memEmail}
