mapper.select:
select mem_code
from mem
where mem_email = #{memEmail}

mapper.update:
UPDATE proj_mem
SET prtpt_aprov_code = 'Y'
WHERE mem_code = #{memCode}
  AND proj_id = #{projId}
  AND prtpt_aprov_code = 'N'

mapper.delete:
DELETE FROM invitation
WHERE
    mem_code = #{memCode}
  AND proj_id =#{projId}
