mapper.select:
SELECT PROJ_BD_ID , WRITER , PROJ_BD_TITLE , PROJ_BD_WRITE_DATE FROM ( SELECT ROWNUM RN , A.PROJ_BD_ID , A.WRITER , A.PROJ_BD_TITLE , A.PROJ_BD_WRITE_DATE FROM( SELECT PROJ_BD_ID , (SELECT M.MEM_NAME FROM MEM M WHERE P.WRITER = M.MEM_CODE) AS WRITER , PROJ_BD_TITLE , PROJ_BD_WRITE_DATE FROM PROJ_BD P WHERE DELETE_YN = 'n' AND PROJ_ID = #{projId}
AND PROJ_BD_TITLE LIKE #{keyword}
AND PROJ_BD_CTS LIKE #{keyword}
ORDER BY PROJ_BD_ID DESC) A )
WHERE RN > ( #{pageNum} - 1 ) * #{amount} AND RN <= #{pageNum} * #{amount}


	SELECT COUNT(PROJ_BD_ID)
	FROM PROJ_BD
	WHERE PROJ_ID = #{projId}
	AND DELETE_YN = 'n'


            AND PROJ_BD_TITLE LIKE #{keyword}


            AND PROJ_BD_CTS LIKE #{keyword}

mapper.update:
UPDATE PROJ_BD
SET PROJ_BD_TITLE = #{projBdTitle}, PROJ_BD_CTS = #{projBdCts}, PROJ_BD_WRITE_DATE=sysdate
WHERE PROJ_ID = #{projId}
AND PROJ_BD_ID = #{projBdId}
AND WRITER = #{writer}

UPDATE PROJ_BD
SET DELETE_YN = 'y'
WHERE PROJ_ID = #{projId}
AND WRITER = #{writer}
AND PROJ_BD_ID = #{projBdId}

UPDATE PROJ_BD
SET DELETE_YN = 'y'
WHERE PROJ_ID = #{projId}
AND PROJ_BD_ID = #{projBdId}

mapper.select:
		SELECT MEM_AUTH
        FROM MEMBER_AUTH
        WHERE MEM_CODE = #{writer}
        AND PROJ_ID = #{projId}
