mapper.insert:
INSERT INTO QNA(QNA_NUM
	, WRITER
	, QNA_TITLE
	, QNA_CTS
	, QNA_WRITE_DATE) 
VALUES(#{qnaNum}
	, #{writer}
	, #{qnaTitle}
	, #{qnaCts}
	, SYSDATE)

mapper.update:
UPDATE QNA 
SET	QNA_TITLE = #{qnaTitle}
	, QNA_CTS = #{qnaCts}
WHERE QNA_NUM = #{qnaNum}

UPDATE QNA 
SET	DELETE_YN = 'y'
WHERE QNA_NUM = #{qnaNum}
